var carat = "carat";
var nillow = "nillow";
var eyimba = "eyimba";
var cloudBooks = "cloudbooks"

var darkCarat = "img/carat.svg";
var nillowDark = "/img/nillow-dark.svg";
var eyimbaDark = "img/eyimba-dark.svg";
var cloudBookDark = "/img/cloudbooks-dark.svg";

var lightCarat = "img/carat-white.svg";
var nillowWhite = "img/nillow.svg";
var eyimbaWhite = "img/eyimba.svg";
var cloudBookWhite = "/img/cloudbooks.svg";

function initTheme() {
  var e =
    null !== localStorage.getItem("darkSwitch") &&
    "dark" === localStorage.getItem("darkSwitch");
  (darkSwitch.checked = e),
    e
      ? (document.body.setAttribute("data-theme", "dark"),
      (document.getElementById(nillow).src = nillowDark),
      (document.getElementById(eyimba).src = eyimbaDark),
      (document.getElementById(cloudBooks).src = cloudBookDark),
        (document.getElementById(carat).src = lightCarat))
      : (document.body.removeAttribute("data-theme"),
        (document.getElementById(nillow).src = nillowWhite),
        (document.getElementById(eyimba).src = eyimbaWhite),
        (document.getElementById(cloudBooks).src = cloudBookWhite),
        (document.getElementById(carat).src = darkCarat));
}
function resetTheme() {
  darkSwitch.checked
    ? (document.body.setAttribute("data-theme", "dark"),
      localStorage.setItem("darkSwitch", "dark"),
      (document.getElementById(nillow).src = nillowDark),
      (document.getElementById(eyimba).src = eyimbaDark),
      (document.getElementById(cloudBooks).src = cloudBookDark),
      (document.getElementById(carat).src = lightCarat))
    : (document.body.removeAttribute("data-theme"),
      localStorage.removeItem("darkSwitch"),
      (document.getElementById(nillow).src = nillowWhite),
      (document.getElementById(eyimba).src = eyimbaWhite),
      (document.getElementById(cloudBooks).src = cloudBookWhite),
      (document.getElementById(carat).src = darkCarat));
}

var darkSwitch = document.getElementById("darkSwitch");
window.addEventListener("load", function () {
  darkSwitch &&
    (initTheme(),
    darkSwitch.addEventListener("change", function () {
      resetTheme();
    }));
});
